import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import '../Home.css';

const Productos = () => {
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    // Realiza una solicitud a la API para obtener los productos
    fetch('https://dummyjson.com/docs/products')
      .then((response) => response.json())
      .then((data) => setProductos(data))
      .catch((error) => console.error('Error al obtener productos:', error));
  }, []);

  return (
    <div className="home-container">
      <div className='TituloProducto'>
        <br></br>
        <h1>Nuestra selecci√≥n de muebles</h1>
        <br></br>
      </div>
      <div className="product-list">
        {productos.map((product) => (
          <div key={product.id} className="product">
            <img src={product.img1} alt={product.name} className="product-image" />
            <br></br><br></br>
            <div className='Link'>
              <Link to={`/Producto/${product.id}`}>
                <button><p className="product-name">{product.name}</p></button>
              </Link>
            </div>
            <p className="product-price">Precio: ${product.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Productos;
