import React from "react";
import { useParams } from "react-router-dom";
import productos from "../productos.json";
import '../Home.css'; // Asegúrate de importar tu archivo de estilos CSS personalizado aquí

export default function Producto() {const handleGoBack = () => {
  window.history.back(); // Esta función regresará a la página anterior en el historial del navegador
};
  const { id } = useParams();
  const producto = productos.find((p) => p.id === parseInt(id));

  if (producto) {
    return (
      <div className="product-details">
        <br></br><br></br><br></br><br></br><br></br>
        <img src={producto.img1} alt={producto.name} className="product-imageDetalles" />
        <br></br><br></br><br></br><br></br><br></br>
        <div className="product-info">
          <h1 className="product-name">{`Nombre: ${producto.name}`}</h1>
          <p className="product-description">{`Descripción: ${producto.descripcion}`}</p>
          <p className="product-category">{`Categoría: ${producto.categoria}`}</p>
        </div>
        <br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br>
        <br></br><br></br><br></br><br></br><br></br><br></br><br></br><br></br>
        
      
      </div>
    );
  } else {
    return <div>Producto no encontrado</div>;
  }
}
